import {Component, Input} from '@angular/core';
import {AddPetLocationForm} from "./add-pet-location.form";
import {City} from "../../../models/enums/city.enum";
import * as L from "leaflet";
import {MapOptions} from "leaflet";
import {GeocodeUsecase} from "../add-pet-usecases/geocode.usecase";

@Component({
  selector: 'app-pet-location',
  templateUrl: './app-pet-location.component.html',
  styleUrls: ['./app-pet-location.component.scss']
})
export class AppPetLocationComponent {
  constructor(private geocodeUsecase: GeocodeUsecase) {
  }

  @Input() locationForm: AddPetLocationForm = new AddPetLocationForm()
  cities = Object.keys(City).filter((cityEnum) => typeof City[cityEnum as keyof typeof City] === "number");
  map: L.Map = {} as L.Map
  private readonly ICON = L.icon({
    iconUrl: 'data:image/png;base64,'
  })
  private readonly DEFAULT_ZOOM = 6
  private readonly FOCUS_ZOOM = 17
  private readonly DEFAULT_CENTER = {
    center: [52, 19],
    zoom: this.DEFAULT_ZOOM
  }
  mapInitialized = false

  mapInit() {
    if (this.mapInitialized) return
    this.map = L.map("location-map", this.DEFAULT_CENTER as MapOptions)
    const tiles = L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 18,
      minZoom: this.DEFAULT_ZOOM,
      attribution: '&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>'
    });
    tiles.addTo(this.map);
    this.mapInitialized = true
  }

  geocodeLocation() {
    const {city, street, building} = this.locationForm.value
    this.geocodeUsecase.execute(`${city},${street},${building}`).subscribe((res) => {
      this.locationForm.latitude.setValue(res[0].lat)
      this.locationForm.longitude.setValue(res[0].lon)
      this.setMarker(res[0].lat, res[0].lon)
    })
  }

  setMarker(latitude: number, longitude: number) {
    this.map.eachLayer((layer: any) => {
      if (layer instanceof L.Marker) {
        this.map.removeLayer(layer)
      }
    })
    L.marker([latitude, longitude], {icon: this.ICON}).addTo(this.map)
    this.map.setView([latitude, longitude], this.FOCUS_ZOOM)
  }
}
